mutation setDeliveryOption($deliveryOption: DeliveryOptionInput, $forceOverrideProducts: Boolean) {
  manageCheckout(deliveryOption: $deliveryOption, forceOverrideProducts: $forceOverrideProducts) {
    messages {
      category
      target
      key
      args
      message
    }
    checkout {
      id
      deliveryOption {
        address {
          latitude
          longitude
          zipcode
          country
          province
          city
          neighborhood
          street
          number
          addressLine2
        }
        deliveryMethod
        schedule
        scheduleDateTime
        pickupPoc {
          id
          tradingName
          address {
            latitude
            longitude
            zipcode
            country
            province
            city
            neighborhood
            street
            number
            addressLine2
          }
        }
      }
      paymentMethod {
        id
        displayName
      }
    }
  }
}
